  render: () => <Tabs defaultValue="overview" className="w-[500px]">\r
      <TabsList className="grid w-full grid-cols-3">\r
        <TabsTrigger value="overview">概览</TabsTrigger>\r
        <TabsTrigger value="analytics">分析</TabsTrigger>\r
        <TabsTrigger value="settings">设置</TabsTrigger>\r
      </TabsList>\r
      <TabsContent value="overview" className="space-y-4">\r
        <div className="space-y-2">\r
          <h3 className="text-xl font-medium">数据概览</h3>\r
          <div className="grid grid-cols-2 gap-4">\r
            <div className="p-4 border rounded-lg">\r
              <h4 className="font-medium text-blue-600">总用户数</h4>\r
              <p className="text-2xl font-medium">12,345</p>\r
            </div>\r
            <div className="p-4 border rounded-lg">\r
              <h4 className="font-medium text-green-600">活跃用户</h4>\r
              <p className="text-2xl font-medium">8,921</p>\r
            </div>\r
          </div>\r
        </div>\r
      </TabsContent>\r
      <TabsContent value="analytics" className="space-y-4">\r
        <div className="space-y-2">\r
          <h3 className="text-xl font-medium">数据分析</h3>\r
          <div className="h-32 bg-slate-100 rounded-lg flex items-center justify-center">\r
            <p className="text-muted-foreground">图表区域</p>\r
          </div>\r
        </div>\r
      </TabsContent>\r
      <TabsContent value="settings" className="space-y-4">\r
        <div className="space-y-2">\r
          <h3 className="text-xl font-medium">系统设置</h3>\r
          <div className="space-y-3">\r
            <div className="flex items-center justify-between">\r
              <span>邮件通知</span>\r
              <input type="checkbox" defaultChecked />\r
            </div>\r
            <div className="flex items-center justify-between">\r
              <span>推送通知</span>\r
              <input type="checkbox" />\r
            </div>\r
            <div className="flex items-center justify-between">\r
              <span>自动更新</span>\r
              <input type="checkbox" defaultChecked />\r
            </div>\r
          </div>\r
        </div>\r
      </TabsContent>\r
    </Tabs>
